import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Table, Button, Container } from 'react-bootstrap';
import { Link } from 'react-router-dom';

export default function Home() {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    const res = await axios.get('http://localhost:5000/api/books');
    setBooks(res.data);
  };

  const deleteBook = async (id) => {
    if (window.confirm('Are you sure you want to delete this book?')) {
      await axios.delete(`http://localhost:5000/api/books/${id}`);
      fetchBooks();
    }
  };

  return (
    <Container className="mt-4">
      <h1 className="mb-4">Library Books</h1>
      <Table striped bordered hover responsive>
        <thead>
          <tr>
            <th>Title</th><th>Author</th><th>Status</th><th>Issued To</th><th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {books.map(book => (
            <tr key={book._id}>
              <td>{book.title}</td>
              <td>{book.author}</td>
              <td>{book.status}</td>
              <td>{book.issuedTo || '-'}</td>
              <td>
                <Link to={`/edit/${book._id}`}>
                  <Button variant="primary" size="sm" className="me-2">Edit</Button>
                </Link>
                <Button variant="danger" size="sm" onClick={() => deleteBook(book._id)}>Delete</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Container>
  );
}
