import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Form, Button, Container, Card } from 'react-bootstrap';
import { toast } from 'react-toastify';

const EditBook = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [book, setBook] = useState({
    title: '',
    author: '',
    status: 'available',
    issuedTo: ''
  });

  useEffect(() => {
    const fetchBook = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/books/${id}`);
        setBook(res.data);
      } catch (err) {
        toast.error('Failed to load book');
      }
    };

    fetchBook();
  }, [id]);

  const handleChange = (e) => {
    setBook({ ...book, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:5000/api/books/${id}`, book);
      toast.success('Book updated successfully!');
      navigate('/books');
    } catch (err) {
      toast.error('Failed to update book');
    }
  };

  return (
    <Container className="mt-4">
      <Card className="p-4 shadow">
        <h3 className="text-center mb-4">‚úèÔ∏è Edit Book</h3>
        <Form onSubmit={handleSubmit}>
          <Form.Group controlId="title" className="mb-3">
            <Form.Label>üìñ Title</Form.Label>
            <Form.Control
              type="text"
              name="title"
              value={book.title}
              onChange={handleChange}
              required
            />
          </Form.Group>

          <Form.Group controlId="author" className="mb-3">
            <Form.Label>‚úçÔ∏è Author</Form.Label>
            <Form.Control
              type="text"
              name="author"
              value={book.author}
              onChange={handleChange}
              required
            />
          </Form.Group>

          <Form.Group controlId="status" className="mb-3">
            <Form.Label>üìå Status</Form.Label>
            <Form.Select name="status" value={book.status} onChange={handleChange}>
              <option value="available">Available</option>
              <option value="issued">Issued</option>
            </Form.Select>
          </Form.Group>

          <Form.Group controlId="issuedTo" className="mb-4">
            <Form.Label>üë§ Issued To</Form.Label>
            <Form.Control
              type="text"
              name="issuedTo"
              value={book.issuedTo}
              onChange={handleChange}
              placeholder="Leave blank if not issued"
            />
          </Form.Group>

          <div className="d-flex justify-content-between">
            <Button variant="secondary" onClick={() => navigate('/books')}>
              ‚¨ÖÔ∏è Back
            </Button>
            <Button variant="success" type="submit">
              ‚úÖ Update Book
            </Button>
          </div>
        </Form>
      </Card>
    </Container>
  );
};

export default EditBook;
