import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Table, Button, Container, Card } from 'react-bootstrap';
import { Link } from 'react-router-dom';

export default function Home() {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    const res = await axios.get('http://localhost:5000/api/books');
    setBooks(res.data);
  };

  const deleteBook = async (id) => {
    if (window.confirm('Are you sure you want to delete this book?')) {
      await axios.delete(`http://localhost:5000/api/books/${id}`);
      fetchBooks();
    }
  };

  return (
    <Container className="mt-4">
      <Card className="shadow-lg border-0">
        <Card.Body>
          <div className="d-flex justify-content-between align-items-center mb-4">
            <h2 className="fw-bold text-primary">Library Book List</h2>
            <Link to="/add">
              <Button variant="outline-success" className="fw-semibold">➕ Add New Book</Button>
            </Link>
          </div>

          <Table responsive bordered hover className="align-middle text-center">
            <thead className="table-dark">
              <tr>
                <th>Title</th>
                <th>Author</th>
                <th>Status</th>
                <th>Issued To</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {books.map(book => (
                <tr key={book._id}>
                  <td>{book.title}</td>
                  <td>{book.author}</td>
                  <td>
                    <span className={`badge ${book.status === 'Available' ? 'bg-success' : 'bg-warning text-dark'}`}>
                      {book.status}
                    </span>
                  </td>
                  <td>{book.issuedTo || '-'}</td>
                  <td>
                    <Link to={`/edit/${book._id}`}>
                      <Button variant="outline-primary" size="sm" className="me-2">✏️ Edit</Button>
                    </Link>
                    <Button variant="outline-danger" size="sm" onClick={() => deleteBook(book._id)}>🗑️ Delete</Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Card.Body>
      </Card>
    </Container>
  );
}
